import { createSlice } from "@reduxjs/toolkit";


const initialState = [
    {
        id: '1',
        title:'Macbook' ,
        content: 'It is hard',
    },
    {
        id:'2',
        title:'Windows',
        content:'It is Easy'
    }
]

const postsSlice = createSlice({
    name: 'posts',
    initialState,
    reducers:{
        postAdded(state,action){
            state.push(action.payload)
        }

    }
})

//creating and exporting selectAllPosts , so that we can export the changes in it dynamically 
export const selectAllPosts = (state)=>state.posts;

export const {postAdded} = postsSl

export default posstSlice.reducer
